import React from 'react';

interface GridProps {
  source: Array<{
    name: string;
    mailReceivedDate: string;
    solutionSentDate?: string;
    isBackgroundColorRed?: boolean;
  }>;
}

const Grid: React.FC<GridProps> = ({ source }) => {
  return (
    <table style={{ border: '1px solid black', borderCollapse: 'collapse' }}>
      <thead>
        <tr>
          <th>Name</th>
          <th>Mail Received Date</th>
          <th>Solution Sent Date</th>
          <th>Is Background Red</th>
        </tr>
      </thead>
      <tbody>
        {source.map((row, index) => (
          <tr
            key={index}
            style={{
              backgroundColor: row.isBackgroundColorRed ? 'red' : 'white',
            }}
          >
            <td>{row.name}</td>
            <td>{row.mailReceivedDate}</td>
            <td>{row.solutionSentDate || '-'}</td>
            <td>{row.isBackgroundColorRed ? 'Yes' : 'No'}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Grid;
