export default function App() {
  const [today, setToday] = useState(new Date());
  const [limit, setLimit] = useState(5);
  const [result, setResult] = useState(0);

  const handleCheck = () => {
    console.log('Before Control Function Call');
    const miscoloredCount = control(today, limit);
    console.log('Miscolored Rows Count:', miscoloredCount);
    setResult(miscoloredCount);
  };

  return (
    <div>
      <h1>Dgpays Case Study</h1>
      <Grid source={dataList} />
      <div style={{ marginTop: '20px' }}>
        <label style={{ marginRight: '15px' }}>
          Today:
          <input
            type="date"
            onChange={(e) => setToday(new Date(e.target.value))}
            value={today.toISOString().split('T')[0]}
          />
        </label>
        <label style={{ marginRight: '15px' }}>
          Limit:
          <input
            type="number"
            onChange={(e) => setLimit(parseInt(e.target.value))}
            value={limit}
          />
        </label>
        <button onClick={handleCheck}>Check Miscolored Rows</button>
      </div>
      <p>Miscolored Rows: {result}</p>
    </div>
  );
}
