import React, { useState } from 'react';
import './style.css';
import Grid from './grid';
import dataList from './data.json';
import { control } from './control';

export default function App() {
  const [today, setToday] = useState(new Date());
  const [limit, setLimit] = useState(5);
  const [result, setResult] = useState(0);
  const [miscoloredRows, setMiscoloredRows] = useState<string[]>([]);  // Hatalı satırları tutacak dizi

  const handleCheck = () => {
    const { count, miscoloredNames } = control(today, limit);
    setResult(count);
    setMiscoloredRows(miscoloredNames); // Hatalı satır isimlerini güncelliyoruz
  };

  return (
    <div>
      <h1>Dgpays Case Study</h1>
      <Grid source={dataList} />
      <div style={{ marginTop: '20px' }}>
        <label style={{ marginRight: '10px' }}>
          Today : 
          <input
            type="date"
            onChange={(e) => setToday(new Date(e.target.value))}
            value={today.toISOString().split('T')[0]}
            style={{ marginLeft: '5px' }} // ":" ve tarih arasına boşluk ekledik
          />
        </label>
        <label style={{ marginRight: '40px' }}>
          Limit :
          <input
            type="number"
            onChange={(e) => setLimit(parseInt(e.target.value))}
            value={limit}
          />
        </label>
        <button onClick={handleCheck}>Check Miscolored Rows</button>
      </div>
      <p>Miscolored Rows Count: {result}</p>
      {miscoloredRows.length > 0 && (
        <div>
          <h3 style={{ fontWeight: 'normal',  }}>Miscolored Rows Name: </h3>
          <span>{miscoloredRows.join(', ')}</span> {/* İsimleri aynı satırda virgülle ayırarak gösteriyoruz */}
        </div>
      )}
    </div>
  );
}
