import React from 'react';

interface GridProps {
  source: Array<{
    name: string;
    mailReceivedDate: string;
    solutionSentDate?: string;
    isBackgroundColorRed?: boolean;
  }>;
}

const Grid: React.FC<GridProps> = ({ source }) => {
  console.log('Grid Source Data:', source);
  return (
    <table style={{
      borderCollapse: 'collapse',
      width: '30%',
      tableLayout: 'auto', // Kolon genişlikleri içeriğe göre belirlenecek
    }}>
      <tbody>
        {source.map((row, index) => (
          <tr
            key={index}
            style={{
              backgroundColor: row.isBackgroundColorRed ? 'red' : 'white',
            }}
          >
            <td style={{
              padding: '5px',
              borderBottom: '2px solid white',  // Satır altına çizgi ekledik
              borderRight: '2px solid white',  // Hücre sağ tarafına çizgi ekledik
              textAlign: 'left',
              display: 'flex', // Flex ile hizalama
              justifyContent: 'flex-start', // Sol hizalama
            }}>
              {row.name}
            </td>
            <td style={{
              padding: '5px',
              borderBottom: '2px solid white',
              borderRight: '2px solid white',
              textAlign: 'left',
              display: 'flex', // Flex ile hizalama
              justifyContent: 'flex-start', // Sol hizalama
            }}>
              {row.mailReceivedDate}
            </td>
            <td style={{
              padding: '5px',
              borderBottom: '2px solid white',
              textAlign: row.solutionSentDate ? 'left' : 'center', // - varsa ortada olacak
              display: 'flex', // Flex ile hizalama
              justifyContent: row.solutionSentDate ? 'flex-start' : 'center', // Metin varsa sola, yoksa ortaya al
            }}>
              {row.solutionSentDate || '-'}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Grid;
