import React, { useState } from 'react';
import './style.css';
import Grid from './grid';
import dataList from './data.json';
import { control } from './control';

export default function App() {
  const [today, setToday] = useState(new Date());
  const [limit, setLimit] = useState(5);
  const [result, setResult] = useState(0);
  const [updatedData, setUpdatedData] = useState(dataList);

  const handleCheck = () => {
    console.log('Before Control Function Call:', updatedData); // Önceki veri durumu
  
    const miscoloredCount = control(today, limit, updatedData, setUpdatedData);
  
    console.log('After Control Function Call:', updatedData); // Güncellenmiş veri durumu
    setResult(miscoloredCount);
  
    console.log('Miscolored Rows Count:', miscoloredCount); // Hatalı renklendirme sayısı
  };
  

  return (
    <div>
      <h1>Dgpays Case Study</h1>
      <Grid source={updatedData} />
      <div style={{ marginTop: '20px' }}>
        <label>
          Today:
          <input
            type="date"
            onChange={(e) => setToday(new Date(e.target.value))}
            value={today.toISOString().split('T')[0]}
          />
        </label>
        <label>
          Limit:
          <input
            type="number"
            onChange={(e) => setLimit(parseInt(e.target.value))}
            value={limit}
          />
        </label>
        <button onClick={handleCheck}>Check Miscolored Rows</button>
      </div>
      <p>Miscolored Rows: {result}</p>
    </div>
  );
}
