import React from 'react';

interface GridProps {
  source: Array<{
    name: string;
    mailReceivedDate: string;
    solutionSentDate?: string;
    isBackgroundColorRed?: boolean;
  }>;
}

const Grid: React.FC<GridProps> = ({ source }) => {
  console.log('Grid Source Data:', source);
  return (
    <table style={{ 
      border: '1px solid black', 
      borderCollapse: 'collapse', 
      width: '100%',
      tableLayout: 'fixed',
    }}>
      <tbody>
        {source.map((row, index) => (
          <tr
            key={index}
            style={{
              backgroundColor: row.isBackgroundColorRed ? 'red' : 'white',
              display: 'flex', // Using flexbox to align cells closely
              justifyContent: 'flex-start', // Align all items to the left
            }}
          >
            <td style={{ padding: '5px', flex: 1 }}>{row.name}</td>
            <td style={{ padding: '5px', flex: 1 }}>{row.mailReceivedDate}</td>
            <td style={{ padding: '5px', flex: 1 }}>{row.solutionSentDate || '-'}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Grid;
