export function control(today: Date, limit: number): { count: number, miscoloredNames: string[] } {
  const rows = document.querySelectorAll('tbody tr');
  let miscoloredRows = 0;
  const miscoloredNames: string[] = []; // Hatalı satır isimlerini tutacak dizi

  rows.forEach((row, index) => {
    const cells = row.querySelectorAll('td');

    const mailReceivedDate = new Date(cells[1]?.textContent?.trim() || '');
    const solutionSentDate =
      cells[2]?.textContent?.trim() && cells[2]?.textContent?.trim() !== '-'
        ? new Date(cells[2]?.textContent?.trim())
        : today;

    if (isNaN(mailReceivedDate.getTime()) || isNaN(solutionSentDate.getTime())) {
      return;
    }

    const dayDifference = Math.ceil(
      Math.abs(solutionSentDate.getTime() - mailReceivedDate.getTime()) / (1000 * 60 * 60 * 24)
    );

    const computedStyle = window.getComputedStyle(row);
    const isBackgroundRed = computedStyle.backgroundColor === 'rgb(255, 0, 0)';

    // Satır ismini almak
    const rowName = cells[0]?.textContent?.trim();

    // Loglar
    console.log(`Row ${index + 1}:`);
    console.log(`- Mail Received Date: ${mailReceivedDate}`);
    console.log(`- Solution Sent Date: ${solutionSentDate}`);
    console.log(`- Day Difference: ${dayDifference}`);
    console.log(`- Is Background Red: ${isBackgroundRed}`);
    console.log(`- Row Name: ${rowName}`);

    // Yanlış renklendirme kontrolü
    if ((isBackgroundRed && dayDifference <= limit) || (!isBackgroundRed && dayDifference > limit)) {
      miscoloredRows++;
      if (rowName) miscoloredNames.push(rowName); // Hatalı satır ismini ekle
    }
  });

  return { count: miscoloredRows, miscoloredNames }; // Hatalı satır sayısı ve isimlerini döndür
}
