export function control(today: Date, limit: number): number {
    const rows = document.querySelectorAll('tbody tr');
    let miscoloredRows = 0;
  
    rows.forEach((row) => {
      const cells = row.querySelectorAll('td');
  
      if (cells.length < 4) return; // Hücre sayısını kontrol et (hataları önlemek için)
  
      const mailReceivedDate = new Date(cells[1]?.textContent?.trim() || '');
      const solutionSentDate = cells[2]?.textContent?.trim()
        ? new Date(cells[2]?.textContent?.trim())
        : today;
      const isBackgroundRed = cells[3]?.textContent?.trim() === 'Yes';
  
      const dayDifference = Math.ceil(
        Math.abs(solutionSentDate.getTime() - mailReceivedDate.getTime()) / (1000 * 60 * 60 * 24)
      );
  
      // Hatalı renklendirme kontrolü (Koşulların detaylı ayrımı)
      if (isBackgroundRed && dayDifference > limit) {
        // Kırmızı renklendirildi ama gün farkı limitten büyükse
        miscoloredRows++;
      } else if (!isBackgroundRed && dayDifference <= limit) {
        // Kırmızı renklendirilmedi ama gün farkı limitten küçükse
        miscoloredRows++;
      }
    });
  
    return miscoloredRows;
  }
